package zork;import ch.bbw.zork.Command;import org.junit.Test;import static org.junit.Assert.*;public class TestCommand {	Command command = new Command(null);	Command commandWithSecondWord = new Command("go", "north");	@Test	public void testIsUnknown(){		assertTrue(command.isUnknown());	}	@Test	public void hasSecondWord(){		assertTrue(commandWithSecondWord.hasSecondWord());		assertFalse(command.hasSecondWord());	}}