package zork;/** * Author:  Michael Kolling, Version: 1.1, Date: August 2000 * refactoring: Rinaldo Lanza, September 2020 */import ch.bbw.zork.Room;import org.junit.Test;import static org.junit.Assert.assertEquals;public class TestRoom {	@Test	public void testSetDescription(){		Room room = new Room("testDescription");		assertEquals("You are in testDescription.\nExits:", room.longDescription());		assertEquals("testDescription", room.shortDescription());	}	@Test	public void testNextRoom(){		Room room = new Room("test");		room.setExits(room, null, null, null);		assertEquals(room, room.nextRoom("north"));	}}